"use strict";const SUBSCRIBE_NEWSLETTER_URL=BASE_API_URL+"/listmonk/subscribe.php";class Newsletter{constructor(e,t){this._form=e,this._data=t}subscribe(){if(!$(this._form)[0].checkValidity()){$(this._form).find(":input").addClass("show-invalid"),this._data.errorMessage="Please fill in all required fields.";return}this._data.inProgress=!0,this._data.errorMessage="",this._data.success=!1,subscribeToNewsletter(this._data.email,this._data.listId).done(()=>{this.onSubscribeSucceeded()}).fail(e=>{this.onSubscribeFailed(e.responseJSON?.message||"Subscribing to newsletter failed.")})}onSubscribeFailed(e){this._data.success=!1,this._data.errorMessage=e,this._data.inProgress=!1}onSubscribeSucceeded(){this._data.success=!0,this._data.errorMessage="",this._data.inProgress=!1}}function subscribeToNewsletter(e,t){return $.ajax({url:SUBSCRIBE_NEWSLETTER_URL,type:"GET",xhrFields:{withCredentials:!0}}).then(()=>$.ajax({url:SUBSCRIBE_NEWSLETTER_URL,type:"POST",data:{email:e,listid:t},xhrFields:{withCredentials:!0}}))}